// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

// import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import '@openzeppelin/contracts/access/Ownable.sol';
import './Token.sol';

contract TokenSwap is Ownable(msg.sender) {
  address private _admin;
  Token private _tka;
  Token private _tkb;
  uint private _swapRate; // 1 TKA = swapRate TKB

  event Swap(address indexed sender, uint amount, address indexed receiver);

  constructor(address tka_, address tkb_, uint swapRate_) {
    _tka = Token(tka_);
    _tkb = Token(tkb_);
    _swapRate = swapRate_;
  }

  function changeSwapRate(uint swapRate_) external onlyAdmin {
    _swapRate = swapRate_;
  }

  function swap(uint amount) external {
    require(amount > 0, 'Amount is zero');
    uint amountToReceive = amount * _swapRate;
    _tka.transferFrom(msg.sender, address(this), amount);
    _tkb.transfer(msg.sender, amountToReceive);
    emit Swap(msg.sender, amount, address(this));
  }

  modifier onlyAdmin() {
    _checkOwner();
    _;
  }
}

